from langflow.custom import Component
from langflow.io import HandleInput, Output
from langflow.schema import Message, Data
import spacy

class NERExtractorNode(Component):
    display_name = "NER Extractor"
    description = "Extract entities from Message or Data using SpaCy NER."
    icon = "Brain"

    # Cache Spacy model to prevent re-loading every run
    _nlp = None

    inputs = [
        HandleInput(
            name="input_message",
            display_name="Input Message",
            input_types=["Data"],
            required=True,
        ),
    ]

    outputs = [
        Output(name="entity_names", display_name="Extracted Entities (comma-separated)", method="extract_entities_str"),
        Output(name="entity_list", display_name="Extracted Entities (list)", method="extract_entities_list"),
    ]

    @classmethod
    def get_spacy_model(cls):
        """Load spaCy only once."""
        if cls._nlp is None:
            try:
                cls._nlp = spacy.load("en_core_web_sm")
            except OSError:
                import subprocess
                subprocess.run(["python", "-m", "spacy", "download", "en_core_web_sm"])
                cls._nlp = spacy.load("en_core_web_sm")
        return cls._nlp

    def _get_text(self, obj):
        """Extract text safely from Message, Data, or raw values."""
        if isinstance(obj, Message):
            return obj.text

        if isinstance(obj, Data):
            # Langflow Data object holds raw value inside .data
            return str(obj.data)

        if hasattr(obj, "text"):
            return obj.text

        return str(obj)

    def _extract_entities(self, text: str):
        nlp = self.get_spacy_model()
        doc = nlp(text)
        entities = sorted(set([
            ent.text.strip()
            for ent in doc.ents
            if ent.label_ not in ["CARDINAL", "ORDINAL", "DATE", "TIME", "QUANTITY"]
        ]))
        return entities

    def extract_entities_str(self) -> str:
        text = self._get_text(self.input_message)
        print(type(text))
        entities = self._extract_entities(text)
        self.status = f"Extracted {len(entities)} entities"
        return ", ".join(entities)

    def extract_entities_list(self) -> list:
        print(self.input_message)
        text = self._get_text(self.input_message)
        entities = self._extract_entities(text)
        self.status = f"Extracted {len(entities)} entities"
        return entities
